mapper.select:
SELECT * FROM (
SELECT p.id AS product_id, p.name, p.company, 'saving' AS type, tmp.miss_amount FROM ( SELECT product1_id AS product_id, product1_miss_amount AS miss_amount, rec_date FROM rec_item WHERE product1_status = '회고' UNION ALL SELECT product2_id, product2_miss_amount, rec_date FROM rec_item WHERE product2_status = '회고' UNION ALL SELECT product3_id, product3_miss_amount, rec_date FROM rec_item WHERE product3_status = '회고' UNION ALL SELECT product4_id, product4_miss_amount, rec_date FROM rec_item WHERE product4_status = '회고' UNION ALL SELECT product5_id, product5_miss_amount, rec_date FROM rec_item WHERE product5_status = '회고' ) tmp JOIN savings p ON tmp.product_id = p.id WHERE MONTH(tmp.rec_date) = #{month} AND YEAR(tmp.rec_date) = #{year}
UNION
SELECT p.id AS product_id, p.name, p.company, 'fund' AS type, tmp.miss_amount FROM ( SELECT product1_id AS product_id, product1_miss_amount AS miss_amount, rec_date FROM rec_item WHERE product1_status = '회고' UNION ALL SELECT product2_id, product2_miss_amount, rec_date FROM rec_item WHERE product2_status = '회고' UNION ALL SELECT product3_id, product3_miss_amount, rec_date FROM rec_item WHERE product3_status = '회고' UNION ALL SELECT product4_id, product4_miss_amount, rec_date FROM rec_item WHERE product4_status = '회고' UNION ALL SELECT product5_id, product5_miss_amount, rec_date FROM rec_item WHERE product5_status = '회고' ) tmp JOIN fund p ON tmp.product_id = p.id WHERE MONTH(tmp.rec_date) = #{month} AND YEAR(tmp.rec_date) = #{year}
UNION
SELECT p.id AS product_id, p.name, p.company, 'pension' AS type, tmp.miss_amount FROM ( SELECT product1_id AS product_id, product1_miss_amount AS miss_amount, rec_date FROM rec_item WHERE product1_status = '회고' UNION ALL SELECT product2_id, product2_miss_amount, rec_date FROM rec_item WHERE product2_status = '회고' UNION ALL SELECT product3_id, product3_miss_amount, rec_date FROM rec_item WHERE product3_status = '회고' UNION ALL SELECT product4_id, product4_miss_amount, rec_date FROM rec_item WHERE product4_status = '회고' UNION ALL SELECT product5_id, product5_miss_amount, rec_date FROM rec_item WHERE product5_status = '회고' ) tmp JOIN pension p ON tmp.product_id = p.id WHERE MONTH(tmp.rec_date) = #{month} AND YEAR(tmp.rec_date) = #{year}
UNION
SELECT p.id AS product_id, p.name, p.company, 'deposit' AS type, tmp.miss_amount FROM ( SELECT product1_id AS product_id, product1_miss_amount AS miss_amount, rec_date FROM rec_item WHERE product1_status = '회고' UNION ALL SELECT product2_id, product2_miss_amount, rec_date FROM rec_item WHERE product2_status = '회고' UNION ALL SELECT product3_id, product3_miss_amount, rec_date FROM rec_item WHERE product3_status = '회고' UNION ALL SELECT product4_id, product4_miss_amount, rec_date FROM rec_item WHERE product4_status = '회고' UNION ALL SELECT product5_id, product5_miss_amount, rec_date FROM rec_item WHERE product5_status = '회고' ) tmp JOIN deposit p ON tmp.product_id = p.id WHERE MONTH(tmp.rec_date) = #{month} AND YEAR(tmp.rec_date) = #{year}
UNION
SELECT p.id AS product_id, p.name, p.company, 'forex' AS type, tmp.miss_amount FROM ( SELECT product1_id AS product_id, product1_miss_amount AS miss_amount, rec_date FROM rec_item WHERE product1_status = '회고' UNION ALL SELECT product2_id, product2_miss_amount, rec_date FROM rec_item WHERE product2_status = '회고' UNION ALL SELECT product3_id, product3_miss_amount, rec_date FROM rec_item WHERE product3_status = '회고' UNION ALL SELECT product4_id, product4_miss_amount, rec_date FROM rec_item WHERE product4_status = '회고' UNION ALL SELECT product5_id, product5_miss_amount, rec_date FROM rec_item WHERE product5_status = '회고' ) tmp JOIN forex p ON tmp.product_id = p.id WHERE MONTH(tmp.rec_date) = #{month} AND YEAR(tmp.rec_date) = #{year}
) all_products ORDER BY miss_amount DESC LIMIT 3

SELECT price
FROM forex_track
WHERE product_id = #{productId}
  AND tracking_date = #{recDate}
  AND currency = #{currency}

SELECT price
FROM forex_track
WHERE product_id = #{productId}
AND currency = #{currency}
AND tracking_date <= #{recDate}
ORDER BY tracking_date DESC
LIMIT 1
